SELECT COUNT(*) FROM "subtask_time_tracking" LEFT JOIN "subtasks" ON "subtasks"."id"="subtask_time_tracking"."subtask_id" LEFT JOIN "tasks" ON "tasks"."id"="subtasks"."task_id" LEFT JOIN "users" ON "users"."id"="subtask_time_tracking"."user_id" LEFT JOIN "projects" ON "projects"."id"="tasks"."project_id" WHERE subtask_time_tracking.is_billable = ? AND subtask_time_tracking.start >= ? AND subtask_time_tracking.start <= ? ORDER BY tasks.project_id ASC, subtasks.task_id ASC, subtask_time_tracking.subtask_id ASC, subtask_time_tracking.start ASC
